    tolerance = 1e-4)
  expect_equal(
    output_18109[["a_num_2"]],
    c(Inf, 0.4, NaN, 0.6375, Inf, 0.04, 0, 1),
    tolerance = 1e-4)
  expect_equal(
    output_18109[["an_int"]],
    c(3.44444, 3, 2.50555, 4, 8, 1, 1, 0),
