    colnames(dplyr::group_keys(output_18109)),
    character(0),
    fixed = TRUE)

  # Testing summarize_metrics(data = NA, cols = NULL, na.r...
  # Testing side effects
  expect_error(
    xpectr::strip_msg(summarize_metrics(data = NA, cols = NULL, na.rm = TRUE, inf.rm = TRUE)),
